import "./App.css";
// Importando una imagen
import freeCodeCampLogo from "./imgs/logo.png";
// Importando el componente boton
import Boton from "./components/Boton";
// Agregando el contador
import { Contador } from "./components/Contador.jsx";

import { useState } from "react";

function App() {
  // HOOKS
  // useState
  const [numClics, setNumClics] = useState(0);

  const manejarClic = () => {
    setNumClics(numClics + 1);
  };

  const reiniciarContador = () => {
    console.log("Reiniciar");
  };

  return (
    <div className="App">
      <div className="freecodecamp-logo-contenedor">
        <img
          className="freecodecamp-logo"
          // Llamando a la imagen
          src={freeCodeCampLogo}
          alt="logo freecodecamp"
        />
      </div>
      <div className="contenedor-principal">
        <Contador contador={numClics} />
        <Boton texto="Clic" esBotonClic={true} manejarClic={manejarClic} />
        <Boton
          texto="Reiniciar"
          esBotonClic={false}
          manejarClic={reiniciarContador}
        />
      </div>
    </div>
  );
}

export default App;
